    public static void solveSantaProblem(String str) {
        int floor = 0;
        int firstTimeEnterBasement = 0;
        char[] directions = str.toCharArray();

        for (int i = 0; i < directions.length; i++) {
            if (directions[i] == '(') {
                floor++;
            } else if (directions[i] == ')') {
                floor--;
            }

            if (floor == -1 && firstTimeEnterBasement == 0) {
                firstTimeEnterBasement = i + 1; // +1 because the problem considers the first character as 1 (not 0)
            }
        }

        System.out.println("Santa ends at floor: " + floor);
        System.out.println("Santa first enters the basement at character position: " + firstTimeEnterBasement);
    }
